const loader=document.querySelector(".loader");window.addEventListener("load",function(){loader.style.display="none"});const text=document.querySelector("#text"),text1=document.querySelector("#text1"),Astro=document.querySelector("#Astro"),dron=document.querySelector(".dron");window.addEventListener("scroll",function(){var e=window.scrollY;text.style.marginRight=2*e+"px",text1.style.marginLeft=2*e+"px",dron.style.marginTop=1.04*e+"px",dron.style.marginLeft=-1*e+"px",Astro.style.marginButtom=1.04*e+"px",Astro.style.marginLeft=+e+"px"});const sec=document.querySelector("#sec"),nav=document.querySelector("#nav"),observer=new IntersectionObserver(e=>{1==e[0].isIntersecting?nav.classList.add("sticky"):nav.classList.remove("sticky")},{root:null,rootMargin:"",threshold:""});observer.observe(sec);const services=document.querySelector(".services"),Fline=document.querySelector(".f-line"),Lineobserver=new IntersectionObserver(e=>{1==e[0].isIntersecting?Fline.classList.add("try"):Fline.classList.remove("try")},{root:null,rootMargin:"",threshold:""});Lineobserver.observe(services);const web=document.querySelector(".web");services.addEventListener("scroll",function(){var e=window.scrollY;web.style.marginLeft=+e+"px"});